import { default as express } from "express";
import { router as accountRoutes } from "./account.js";
import { router as userRoutes } from "./user.js";
import { router as categoryRoutes } from "./category.js";
import { router as productRoutes } from "./product.js";
import { router as purchaseRoutes } from "./purchase.js";
import { router as saleRoutes } from "./sale.js";
import { router as reportsRoutes } from "./reports.js";
import { router as orgReportsRoutes } from "./orgReports.js";
import { router as supplierRoutes } from "./supplier.js";
import { router as transferRoutes } from "./transfer.js";
import { router as stockHistoryRoutes } from "./stockHistory.js";
import { router as customerRoutes } from "./customer.js";
import { router as clientRoutes } from "./client.js";
import { router as clientPaymentRoutes } from "./clientPayment.js";
import { router as userPaymentRoutes } from "./userPayment.js";
import { router as userSalaryRoutes } from "./userSalary.js";
import { router as attendanceRoutes } from "./attendance.js";
import { router as profileRoutes } from "./profile.js";
import { router as workLogRoutes } from "./workLog.js";
export const router = express.Router();
router.use("/account", accountRoutes);
router.use("/users", userRoutes);
router.use("/categories", categoryRoutes);
router.use("/products", productRoutes);
router.use("/purchases", purchaseRoutes);
router.use("/sales", saleRoutes);
router.use("/reports", reportsRoutes);
router.use("/org-reports", orgReportsRoutes);
router.use("/suppliers", supplierRoutes);
router.use("/transfers", transferRoutes);
router.use("/stocks", stockHistoryRoutes);
router.use("/customers", customerRoutes);
router.use("/clients", clientRoutes);
router.use("/client-payment", clientPaymentRoutes);
router.use("/user-payment", userPaymentRoutes);
router.use("/user-salary", userSalaryRoutes);
router.use("/attendance", attendanceRoutes);
router.use("/profile", profileRoutes);
router.use("/work-log", workLogRoutes);
